import UIKit

protocol EvaluatedForManagerBusinessLogic
{
    func doSomething(request: EvaluatedForManager.Something.Request)
    func showEvaluatedManager(token : String)
}

protocol EvaluatedForManagerDataStore
{
    //var name: String { get set }
}

class EvaluatedForManagerInteractor: EvaluatedForManagerBusinessLogic, EvaluatedForManagerDataStore
{
    var presenter: EvaluatedForManagerPresentationLogic?
    var worker: EvaluatedForManagerWorker?
    //var name: String = ""
    
    // MARK: Do something
    
    func doSomething(request: EvaluatedForManager.Something.Request)
    {
        worker = EvaluatedForManagerWorker()
        worker?.doSomeWork()
        
        let response = EvaluatedForManager.Something.Response()
        presenter?.presentSomething(response: response)
    }
    
    func showEvaluatedManager(token : String) {
        worker = EvaluatedForManagerWorker()
        worker?.showManagerEvaluationsDetails(token: token).then(){
            evaluatedManager in
            print(evaluatedManager.content!)
            self.presenter?.showEvaluatedManager(responses: evaluatedManager)
        }.catch{
            error in
            print(error)
        }
    }
}
